
public class ClaseStringIIVídeo12_ll {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//comparar si dos string son iguales
		
		String alumno1 = "david";
		String alumno2= "David";
		
		System.out.println(alumno1.equals(alumno2));
		System.out.println(alumno1.equalsIgnoreCase(alumno2));//ignora si son mayusculas o minisculas
		
	}

}
