
public class Decalraciones_Operaciones {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
		int b;
		double e=5;
		double f=7;
		b=7;
		 
		int c = b+a;
		//incrementar en 1 la variable c
		c++;		
		//incrementar en 6 la variable c
		c+=6;
		//si queremos trabajar con decimales declaramos la varible en dooble
		double d = f/e;
		System.out.println(c);
		System.out.println(d);
	}
	

}
